import java.util.Arrays;import java.util.List;import java.util.stream.Collectors;public class StartWith {    public static void main(String[] args) {        List<Integer> list = Arrays.asList(111,222,22,202,567,620,27,90,89,10);        System.out.println("Start with : "                +list.stream()                .map(e -> String.valueOf(e))                .filter(e -> e.startsWith("2"))                .map(Integer::valueOf)                .collect(Collectors.toList()));    }}